using UnityEngine;using UnityEditor;using Threeyes.AssetManager;[CustomEditor(typeof(AssetTextureManager), false)]public class AssetTextureManagerInspector : AssetFileManagerBaseInspector<AssetTextureManager, Texture2D>{    public override void OnInspectorGUI()    {        AssetTextureManager aim = target as AssetTextureManager;                serializedObject.Update();//Update GUI        SerializedProperty sp = serializedObject.FindProperty("DefaultTexture");        if (sp != null)        {            EditorGUILayout.PropertyField(sp);            Texture2D texture = sp.objectReferenceValue as Texture2D;            RecordUndo(() =>            {                aim.DefaultTexture = texture;            });        }        serializedObject.ApplyModifiedProperties();        base.OnInspectorGUI();    }}